define('views/ogSettingsForm',[
        'backbone',
        'hbs!templates/form',
        'appMan'
    ],
    function( Backbone, FormTmpl, AppMan) {
        'use strict';

        /* Return a ItemView class definition */
        return Backbone.Marionette.ItemView.extend({

            initialize: function() {
                console.log("initialize a OgSettingsForm ItemView");
            },

            template: FormTmpl,

            /* ui selector cache */
            ui: {
                formContainer: '.form-container',
                form: '.form',
                formResultsBox: '.form-results-box'
            },

            /* Ui events hash */
            events: {
            },

            onBeforeRender: function(){
                this.updateOgImagesProperties();
            },
            /* on render callback */
            onRender: function() {
                var self = this;
                var ogImagesSchema = Schemas.ogImagesSchema.properties;
                var formOptions = getFormViewOptions(ogImagesSchema, {
                    events: {

                    }
                });
                _.findWhere(formOptions, {type: "actions"}).items = [
                    {
                        "type": "submit",
                        "title": 'Update OG images (draft)'
                    }
                ];
                self.ui.formContainer.find('.form, .form-results-box').html('');
                self.ui.form.jsonForm({
                    schema: ogImagesSchema,
                    form: formOptions,
                    value: QuizData.ogImages,
                    onSubmit: function (errors, values) {
                        if (errors) {
                            self.ui.formResultsBox.html('<p>Some error! Please check if you filled in the details correctly</p>');
                        }
                        else {
                            QuizData.ogImages = values;
                            self.trigger('form:submitted');
                            self.ui.formResultsBox.html('<div class="alert alert-success"><strong>Updated draft!</strong></div>');
                        }
                    }
                });
                if(!this.useOgForResults()) {
                    self.ui.form.append('<br><div class="alert alert-info">For quizzes with user profile images on results, no OG images for results are needed.</div>');
                }
            },
            templateHelpers: function(){
                var self = this;
                return {

                };
            },
            updateOgImagesProperties: function() {
                if(!this.useOgForResults()) {
                    //For quizzes with user images on results, no OG images for results are needed
                    return;
                }
                var resultItems = _.pluck(QuizData.results, 'id');
                _.each(resultItems, function(resultId){
                    var resultText = getResultTextFromId(resultId);
                        Schemas.ogImagesSchema.properties[resultId] = {
                        title: resultText + ' (1200 x 630) (Optional!)',
                        description: 'OG image for the result: "' + resultText + '" (Displayed on Facebook when a user shares his result page)',
                        type: 'image'
                    }
                });
            },
            useOgForResults: function () {
                if(AppMan.reqres.request('quiz').get('settings').addUserPicInResults) {
                    //For quizzes with user images on results, no OG images for results are needed
                    return false;
                }
                return true;
            }
        });
    });

define('controllers/quiz',[
	'backbone',
	'underscore',
	'appMan',
		'views/tabsLinks',
		'views/tabsContents',
		'collections/tabs',
		'views/detailsForm',
		'views/resultsForm',
		'views/questionsForm',
		'views/ogSettingsForm'
],
function( Backbone, _, AppMan, TabsLinksView, TabsContentsView, Tabs, DetailsFormView, ResultsFormView, QuestionsFormView, OgSettingsFormView) {
    'use strict';
	
	function scrollToTop() {
		$(window).scrollTop(App.quizCanvas.el.offsetTop);
	}
	
	function stripSlashes(url) {
		return url.replace(/\/+$/, "");
	}

	var QuizController = Backbone.Marionette.Controller.extend({

		initialize: function( options ) {
			console.log('initialize a Quiz Controller');
			var self = this;
			App.quizController = this;
			this.quiz = options.quiz;

			AppMan.reqres.setHandler('quiz', function(){
				return App.quizController.quiz;
			});
			AppMan.reqres.setHandler('quiz:meta', function(property){
				if(property) {
					return App.quizMeta[property];
				} else {
					return App.quizMeta;
				}
			});
			AppMan.reqres.setHandler('quiz:viewQuizUrl', function(){
				var url, 
					user = AppMan.reqres.request('user');
				url = AppMan.reqres.request('quiz:meta', 'viewQuizUrl');
				return url;
			});

			AppMan.reqres.setHandler('quiz:questions', function(){
				return self.quiz.get('questions');
			});
			AppMan.reqres.setHandler('quiz:totalQuestions', function(){
				return self.quiz.get('questions').length;
			});
			AppMan.reqres.setHandler('quiz:get-question', function(index){
				var questions = AppMan.reqres.request('quiz:questions');
				if(questions[index]) {
					return questions[index];
				}else {
					return false;
				}
			});
		},
		createEditQuiz: function(){
			$('#quizEditor').removeClass('hidden');
			$('#quizEditorLoading').addClass('hidden');
			var tabs = ['Basic Details', 'Results', 'Questions', 'OG settings'];
			var tabsArray = [];
			_.each(tabs, function(tab){
				tabsArray.push({title: tab});
			});

			AppMan.reqres.setHandler('tabs', function(){
				return tabsArray;
			});

			var tabs = AppMan.reqres.request('tabs');
			var tabsCollection = new Tabs(tabs);
			var tabsLinksView = new TabsLinksView({
				collection: tabsCollection
			});

			var footerTabsLinksView = new TabsLinksView({
				el: '#footerTabLinks',
				collection: tabsCollection
			});

			var tabsContentsView = new TabsContentsView({
				collection: tabsCollection
			});
			function onTabShown(tabId){
				AppMan.trigger('tab:shown', tabId);
			}
			tabsLinksView.on('tab:shown', function(tabId){
				onTabShown(tabId);
			});
			footerTabsLinksView.on('tab:shown', function(tabId){
				onTabShown(tabId);
			});

			AppMan.reqres.setHandler('formView', function(tabId){
				switch(tabId) {
					case "basic-details":
						return DetailsFormView;
					case "results":
						return ResultsFormView;
					case "questions":
						return QuestionsFormView;
					case "og-settings":
						return OgSettingsFormView;
				}
			});

			AppMan.on('tab:shown', function(tabId) {
				$(window).scrollTop(0);
				App.quizController.currentTab = tabId;
			});
			AppMan.on('tab:shown', function(tabId){
				var view = AppMan.reqres.request('tabContentView:' + tabId);
				if(!view.formView) {
					var formView = AppMan.reqres.request('formView', tabId);
					view.formView = new formView({
						el: view.$('.form-container')
					});
				}
				view.formView.render();
				$(window).trigger('quiz-editor-tab-shown', tabId);
			});
			tabsContentsView.render();
			tabsLinksView.render();
			footerTabsLinksView.render();
			tabsLinksView.activateFirst();

			//AppMan.reqres.setHandler('formView', self.getTabId()
		},
		all: function(){
			//alert('default route fired');
		}
	});

	return QuizController;
});

